    """Return the largest prime factor of n. Assume n > 1 and is not a prime."""
    largest_factor = 1
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            if is_prime(i):
                largest_factor = i
            if is_prime(n // i):
                largest_factor = n // i
    return largest_factor

