def fix_spaces(text):
  result = []
  count = 0
  for char in text:
    if char == ' ':
      count += 1
      if count > 2:
        result.append('-')
        count = 0
      else:
        result.append('_')
    else:
      result.append(char)
      count = 0
  return ''.join(result)
